sys_variable_value.value:
(function execute(inputs, outputs) {
    var table_name = inputs.table_name;
    var status_label = inputs.status_label;

    var gr = new GlideRecord('sys_choice');
    gr.addQuery('name', table_name); 
    gr.addQuery('element', 'state'); 
    gr.addQuery('label', status_label); 
    gr.query(); 

    if (gr.next()) {
        outputs.state_value = gr.getValue('value');
    }
    else {
        outputs.state_value = 99;
    }
})(inputs, outputs);

